import "../styles/App.scss";
import { useState } from "react";

const [name, setName] = useState("");
function App(ev) {
  const handleName = () => {
    setName(ev.currentTarget.value);
  };

  return (
    <div>
      <input type="text" name="" id="" value={name} onChange={handleName} />
    </div>
  );
}

export default App;
