import "../styles/App.scss";
import { useEffect, useState } from "react";

function App() {
  const [name, setName] = useState("");
  const [movies, setMovies] = useState([]);

  //use effect cuando no depende de una acción del usuario.
  useEffect(() => {
    fetch(
      "https://beta.adalab.es/curso-intensivo-fullstack-recursos/apis/adamdb-v1/books_with_id.json"
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setMovies(data);
      });
  }, []);

  const handleName = (ev) => {
    setName(ev.currentTarget.value);
  };

  const htmlMovies = movies.map((movieItem) => {
    return <li key={movieItem.id}>{movieItem.title}</li>;
  });

  return (
    <div>
      <input type="text" name="" id="" value={name} onChange={handleName} />
      <p>{htmlMovies}</p>
    </div>
  );
}

export default App;
